import { getByText } from '@testing-library/dom'
import { useEffect, useState } from 'react'
import '../css/AnimationTitle.css'

const AnimationTitle = () => {
  const [animationClass, setAnimationClass] = useState('')

  const getSpans = (text) => {
    const spans = []
    let s = 0.00
    let style = {}
    for (let i=0; i<text.length; i++){
        let letter = text[i] 
        s += 0.06
        style = {
          "animation-delay": s+"s"
        };
        spans.push(<span class={"char char-"+i} style={style}>{(letter === 's') ? letter : '&nbsp;'}</span>)
    }
    return spans
  }

  useEffect(() => {   
    setAnimationClass('is-animated')
  },[])

  return (
    <div>      
        <div class={"demo-inner "+animationClass}>
          <div data-animate="slide-in">
            {getSpans(
              'Testo del mio beatissimo cazzo'
            )}
          </div>
        </div>
    </div>
  );
}

export default AnimationTitle;
