import { Col, Image } from "react-bootstrap";
import '../css/ProductCard.css'

const ProductCard = ({product}) => {

  return (
    <Col md={6} xs={12} >
        <div 
          className='contain-card-image'
          backgroundImage={product.image.base64}
          >
          <div className='contain-card-name'>{product.name}</div>
          <div className='container-card-description'>
            <strong>Ingredienti:</strong>
            <p>
              {product.description}
            </p>
          </div>
          <div className="contain-card-price">{product.price}{product.currency}</div>
        </div>

    </Col>

  );
}

export default ProductCard;
