import React from 'react'
import FileBase64 from 'react-file-base64';

const ProductForm = ( {handleSubmit} ) => {

  const [image, setImage] = useState({});
  const [form, setForm] = useState({
    name: '',
    price: '',
    image: ''
  });

  const setFile = (file) => {
    setImage(file)
    setForm({
      ...form,
      ["image"]: file
    })
  }

  const handleChange = (target) => {
    setForm({
      ...form,
      [target.name]: target.value
    })
  }

  return(
    <div>
      <label>
        <p>Name</p>
        <input type="text" name="name" onChange={e => handleChange(e.target)} />
      </label>
      <label>
        <p>Prezzo</p>
        <input type="number" name="price" onChange={e => handleChange(e.target)} />
      </label>
      <label>
        <p>Immagine</p>
        <FileBase64
          onDone={ setFile.bind(this) } />
      </label>
      <div className="text-center">
        <img src={image.base64} />
      </div>
      <div>
        <button type="submit">Submit</button>
      </div>
    </div>
  )

}

export default ProductForm
