import React, { useEffect, useState } from 'react'
import { useParams, useHistory } from 'react-router-dom'
import { Button, Container, Nav } from 'react-bootstrap'
import { getProduct, deleteProduct } from '../../api/products'

const ShowProductPage = () => {
  const history = useHistory()
  const { id } = useParams()
  const [product, setProduct] = useState({});
  const [image, setImage] = useState({});

  const fetchDataProduct = async () => {
    const resProduct = await getProduct(id)
    setImage(resProduct.image)
    setProduct(resProduct)
  }

  useEffect( () => {
    fetchDataProduct()
  }, [])

  const removeProduct = async () => {
    if(await deleteProduct(product.id)){
      history.push("/admin/dashboard/")
    }
  }

  return (
    <Container>
      <h1>{product.name}</h1>
      <h1>{product.price}</h1>
      <img src={image.base64} /> 
      <Nav.Link key={product.id} href={"/admin/product/edit/"+product.id}>Modifica</Nav.Link>
      <Button onClick={removeProduct}>Rimuovi</Button>
    </Container>
  )

}

export default ShowProductPage
