import React, { useState } from 'react'
import { Form, Button, Col, Image } from 'react-bootstrap';
import FileBase64 from 'react-file-base64';

const ProductForm = ( { handleSubmit, form, setForm, image, setImage } ) => {

  const [errors, setErrors] = useState({})

  const setFile = (file) => {
    setImage(file)
    setForm({
      ...form,
      ["image"]: file
    })
  }

  const isEmptyImage = (obj) => {
    for(var prop in obj) {
      if(obj.hasOwnProperty(prop)) {
        return false;
      }
    }
    return JSON.stringify(obj) === JSON.stringify({});
  }

  const controlForm = (form) => {

    //validate handle react / react hook / yup / ..
    for (const [key, value] of Object.entries(form)) {
      if(!value){
        setErrors({ [key]: "required*"})
        return false
      } 
    }
    // lodash _.isEmpty({});
    if(isEmptyImage(form.image)){
      setErrors({ ['image']: "required*"})
      return false 
    }

    return true
  }

  const handleChange = (target) => {
    setForm({
      ...form,
      [target.name]: target.value
    })
  }

  const submitForm = e => {
    if(controlForm(form)){
      handleSubmit(e, {...form})
    }
  }

  return(
    <Form style={{'margin-top': '50px'}}>
      <Form.Row>
        <Form.Group as={Col} controlId="formGridName">
          <Form.Label>Nome*</Form.Label>
          <Form.Control placeholder="Nome" name="name" value={form.name} onChange={e => handleChange(e.target)} />
          <p>{errors.name}</p>
        </Form.Group>

        <Form.Group as={Col} controlId="formGridPrice">
        <Form.Label>Prezzo Iniziale*</Form.Label>
          <Form.Control type='number' placeholder="Prezzo" name="initialPrice" value={form.initialPrice} onChange={e => handleChange(e.target)} />
          <p>{errors.initialPrice}</p>
        </Form.Group>

        <Form.Group as={Col} controlId="formGridState">
          <Form.Label>Valuta*</Form.Label>
          <Form.Control as="select" name='currency' value={form.currency} onChange={e => handleChange(e.target)}>
            <option></option>
            <option>€</option>
            <option>$</option>
            <option>£</option>
          </Form.Control>
          <p>{errors.currency}</p>
        </Form.Group>
      </Form.Row>

      {/* <Form.Group controlId="exampleForm.ControlTextarea">
        <Form.Label>Descrizione</Form.Label>
        <Form.Control as="textarea" rows={3} name="description" value={form.description} onChange={e => handleChange(e.target)} />
      </Form.Group>
       */}
      <Form.Row>
        <Form.Group as={Col} controlId="Image">
          <Form.Label>Immagine*</Form.Label>
          <br />
          <FileBase64
            onDone={ setFile.bind(this) } />
          <p>{errors.image}</p>
        </Form.Group>
        <Form.Group as={Col}>
          <Image src={image.base64} />
        </Form.Group>
      </Form.Row>

      <Button variant="primary" type="button" onClick={submitForm}>
        Salva
      </Button>
    </Form>  
    )

}

export default ProductForm
