import { Card, Col } from "react-bootstrap";
import '../css/ProductCard.css'

const ProductCard = ({product}) => {

  return (
    <Col md={6} xs={12} >
      <Card>
        <div className='contain-card-image'>
          <Card.Img variant="top" src={product.image.base64} />
        </div>
        <Card.Body>
          <Card.Title>{product.name}</Card.Title>
          <Card.Text>
            {product.description}
          </Card.Text>
        </Card.Body>
      </Card>
    </Col>

  );
}

export default ProductCard;
