import React, { useEffect, useState } from 'react'
import { useParams, useHistory } from 'react-router-dom'
import { Container } from 'react-bootstrap'
import { getProduct, editProduct } from '../../api/products'


const EditProductPage = () => {
  const history = useHistory()
  const { id } = useParams()
  const [form, setForm] = useState({})
  // const [image, setImage] = useState({})

  // const setFile = (file) => {
  //   setImage(file)
  //   setForm({
  //     ...form,
  //     ["image"]: file
  //   })
  // }

  const fetchDataProduct = async () => {
    const resProduct = await getProduct(id)
    // setImage(resProduct.image)
    setForm(resProduct)
  }

  const handleSubmit = async e => {
    e.preventDefault(); 
    const resProduct = await editProduct(form);
    history.push("/admin/product/"+resProduct.id)
  }

  useEffect(() => {
    fetchDataProduct()
  }, [])

  return (
    <Container>
      <h1>Modifica {form.name}</h1>
      <ProductForm handleSubmit={handleSubmit} form={form} setForm={setForm} />
    </Container>
  )

}
export default EditProductPage
