import React, { useState } from 'react'
import FileBase64 from 'react-file-base64';

const ProductForm = ( { handleSubmit, form, setForm, image, setImage } ) => {

  const [errors, setErrors] = useState({})

  const setFile = (file) => {
    setImage(file)
    setForm({
      ...form,
      ["image"]: file
    })
  }


  const isEmptyImage = (obj) => {
    for(var prop in obj) {
      if(obj.hasOwnProperty(prop)) {
        return false;
      }
    }

    return JSON.stringify(obj) === JSON.stringify({});
  }

  const controlForm = (form) => {
    for (const [key, value] of Object.entries(form)) {
      if(!value){
        setErrors({ [key]: "required*"})
        return false
      } 
    }

    // lodash _.isEmpty({});
    if(!isEmptyImage(form.image)){
      console.log(form.image)
      setErrors({ ['image']: "required*"})
      return false 
    }



    return false
  }

  const handleChange = (target) => {
    setForm({
      ...form,
      [target.name]: target.value
    })
  }

  const submitForm = e => {
    if(controlForm(form)){
      handleSubmit(e, {...form})
    }
  }

  return(
    <form>
      <label>
        <p>Name</p>
        <input type="text" name="name" value={form.name} onChange={e => handleChange(e.target)} />
        <p>{errors.name}</p>
      </label>
      <label>
        <p>Prezzo</p>
        <input type="number" name="price" value={form.price} onChange={e => handleChange(e.target)} />
        <p>{errors.price}</p>
      </label>
      <label>
        <p>Immagine</p>
        <FileBase64
          onDone={ setFile.bind(this) } />
        <p>{errors.image}</p>
      </label>
      <div className="text-center">
        <img src={image.base64} />
      </div>
      <div>
        <button onClick={submitForm} type="button">Submit</button>
      </div>
    </form>
  )

}

export default ProductForm
