import axios from 'axios'

export const getProducts = () => {
  return axios.get('http://localhost:8080/products').then(res => {
    return JSON.parse(res.data)
  })
}

export const getProduct = (id) => {
  return axios.get(`http://localhost:8080/product/${id}`).then(res => {
    return res.data
  })
}

export const addProduct = (data) => {
  return axios.post('http://localhost:8080/product', data).then(res => {
    return res.data
  })
}

export const editProduct = (data) => {
  return axios.post('http://localhost:8080/edit-product', data).then(res => {
    return res.data
  })
}

export const deleteProduct = (data) => {
  return axios.delete('http://localhost:8080/delete-product', data).then(res => {
    return res.data
  })
}
