import { Image, Table } from "react-bootstrap";
import { Link } from "react-router-dom";

const TableList = ({ products, removeProduct }) => {

  return (
    <Table striped bordered hover>
      <thead>
        <tr>
          <th></th>
          <th>Nome</th>
          <th>Prezzo</th>
          <th>Azioni</th>
        </tr>
      </thead>
      <tbody>
        { 
          products.map(product => (
            <tr key={product.id}>
              <td><Image src={product.image.base64} style={{ width: "100px" }} /></td>
              <td>{product.name}</td>
              <td>{product.price}{product.currency}</td>
              <td>
                <Link className="btn-action" to={"/admin/product/show/"+product.id}>👁️</Link> 
                <Link className="btn-action" to={"/admin/product/edit/"+product.id}>✏️</Link>
                <span className="btn-action" onClick={() => removeProduct(product.id)}>🗑</span>  
              </td>
            </tr>
          )) 
        }
      </tbody>
    </Table>

  );
}

export default TableList;
