import React, { useState } from 'react'
import FileBase64 from 'react-file-base64';

const ProductForm = ( { handleSubmit, form, setForm, image, setImage } ) => {

  const [errors, setErrors] = useState({})

  const setFile = (file) => {
    setImage(file)
    setForm({
      ...form,
      ["image"]: file
    })
  }

  const controlForm = (input) => {
    if(!input.value){
      setErrors({ [input.name]: input.name+"* required"})
    }
    console.log(errors)
  }

  const handleChange = (target) => {
    controlForm(target)
    setForm({
      ...form,
      [target.name]: target.value
    })
  }

  return(
    <form onSubmit={handleSubmit}>
      <label>
        <p>Name</p>
        <input type="text" name="name" value={form.name} onChange={e => handleChange(e.target)} />
        <p>{errors.name}</p>
      </label>
      <label>
        <p>Prezzo</p>
        <input type="number" name="price" value={form.price} onChange={e => handleChange(e.target)} />
        <p>{errors.price}</p>
      </label>
      <label>
        <p>Immagine</p>
        <FileBase64
          onDone={ setFile.bind(this) } />
      </label>
      <div className="text-center">
        <img src={image.base64} />
      </div>
      <div>
        <button type="submit">Submit</button>
      </div>
    </form>
  )

}

export default ProductForm
