import '../css/AnimationTitle.css'

const SELECTOR = {
  DEMO: document.querySelector('.js-demo'),
}

const CLASSES = {
  ANIMATED: `is-animated`
}

SELECTOR.DEMO.addEventListener('click', () => {
  SELECTOR.DEMO.classList.add(CLASSES.ANIMATED);

  const timer = setInterval(() => {
    SELECTOR.DEMO.classList.remove(CLASSES.ANIMATED);
    clearInterval(timer)
  }, 1500)
});

const AnimationTitle = () => {
  const [animationClass, setAnimationClass] = useState('')
  useEffect(() => {
    setAnimationClass('is-animated')
  },[])
  return (
    <div class='contain-animation-text'>      
      <div class="demo js-demo">
        <div class="demo-inner">
          <div data-animate="slide-in"><span class="char-1 char">L</span><span class="char-2 char">O</span><span class="char-3 char">R</span><span class="char-4 char">E</span><span class="char-5 char">M</span><span class="char-6 char">&nbsp;</span><span class="char-7 char">I</span><span class="char-8 char">P</span><span class="char-9 char">S</span><span class="char-10 char">U</span><span class="char-11 char">M</span>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AnimationTitle;
