import React, { useState } from 'react'
import { Container } from 'react-bootstrap'
import { useHistory } from 'react-router';
import { addProduct } from '../../api/products'
import FileBase64 from 'react-file-base64';


const AddProductPage = () => {
  const history = useHistory()
  const [image, setImage] = useState({});
  const [form, setForm] = useState({
    name: '',
    price: '',
    image: ''
  });

  const setFile = (file) => {
    setImage(file)
    setForm({
      ...form,
      ['image']: file
    })
  }

  const handleChange = (target) => {
    setForm({
      ...form,
      [target.name]: target.value
    })
  }

  const handleSubmit = async e => {
    e.preventDefault();
    const resProduct = await addProduct(form);
    history.push("/admin/product/"+resProduct.id)
  }

  return (
    <Container>
      <h1>Aggiungi prodotto</h1>
      <form onSubmit={handleSubmit}>
        <label>
          <p>Name</p>
          <input type="text" onChange={e => handleChange(e.target)} />
        </label>
        <label>
          <p>Prezzo</p>
          <input type="number" onChange={e => handleChange(e.target)} />
        </label>
        <label>
          <p>Immagine</p>
          <FileBase64
            onDone={ setFile.bind(this) } />
        </label>
        <div className="text-center">
          <img src={image.base64} />
        </div>
        <div>
          <button type="submit">Submit</button>
        </div>
      </form>
    </Container>
  )
}


export default AddProductPage
