import React, { useState } from 'react'
import FileBase64 from 'react-file-base64';

const ProductForm = ( props ) => {


  const handleChange = (target) => {
    props.setForm({
      ...form,
      [target.name]: target.value
    })
  }

  const submitform = () => {
    props.handleSubmit({...form})
  }

  return(
    <form>
      <label>
        <p>Name</p>
        <input type="text" name="name" value={props.form.name} onChange={e => handleChange(e.target)} />
      </label>
      <label>
        <p>Prezzo</p>
        <input type="number" name="price" value={props.form.price} onChange={e => handleChange(e.target)} />
      </label>
      <label>
        <p>Immagine</p>
        {/* <FileBase64
          onDone={ props.setFile.bind(this) } /> */}
      </label>
      <div className="text-center">
        <img src={props.image.base64} />
      </div>
      <div>
        <button onClick={submitform} type="button">Submit</button>
      </div>
    </form>
  )

}

export default ProductForm
