// import '../css/Cake.css'
import { Card, Col } from "react-bootstrap";

const ProductCard = ({product}) => {

  return (
    <Col md={6} xs={12} >
      <Card style={{ width: '80%' }}>
        <Card.Img variant="top" src={product.image.base64} />
        <Card.Body>
          <Card.Title>{product.name}</Card.Title>
          <Card.Text>
            {product.description}
          </Card.Text>
        </Card.Body>
      </Card>
    </Col>

  );
}

export default ProductCard;
