import React, { useState } from 'react'
import FileBase64 from 'react-file-base64';

const ProductForm = ( props ) => {

  const defaultData = {
    name: '',
    price: '',
    image: {}
  }

  const formData = props.form ? props.form : defaultData

  const [form, setForm] = useState(formData)

  const handleChange = (target) => {
    setForm({
      ...form,
      [target.name]: target.value
    })
  }

  const submitform = () => {
    props.handleSubmit({...form})
  }

  return(
    <form>
      <label>
        <p>Name</p>
        <input type="text" name="name" value={form.name} onChange={e => handleChange(e.target)} />
      </label>
      <label>
        <p>Prezzo</p>
        <input type="number" name="price" value={form.price} onChange={e => handleChange(e.target)} />
      </label>
      <label>
        <p>Immagine</p>
        {/* <FileBase64 */}
          // onDone={ props.setFile.bind(this) } />
      </label>
      <div className="text-center">
        <img src={props.image.base64} />
      </div>
      <div>
        <button onClick={submitform} type="button">Submit</button>
      </div>
    </form>
  )

}

export default ProductForm
