import React, { useState } from 'react'
import FileBase64 from 'react-file-base64';

const ProductForm = ( { handleSubmit, form, setForm } ) => {
  const [image, setImage] = useState({})

  const setFile = (file) => {
    setImage(file)
    setForm({
      ...form,
      ["image"]: file
    })
  }

  const handleChange = (target) => {
    setForm({
      ...form,
      [target.name]: target.value
    })
  }

  return(
    <form onSubmit={handleSubmit}>
      <label>
        <p>Name</p>
        <input type="text" name="name" value={form.name} onChange={e => handleChange(e.target)} />
      </label>
      <label>
        <p>Prezzo</p>
        <input type="number" name="price" value={form.price} onChange={e => handleChange(e.target)} />
      </label>
      <label>
        <p>Immagine</p>
        <FileBase64
          onDone={ setFile.bind(this) } />
      </label>
      <div className="text-center">
        <img src={image.base64} />
      </div>
      <div>
        <button type="submit">Submit</button>
      </div>
    </form>
  )

}

export default ProductForm
