import React, { useState } from 'react'
import FileBase64 from 'react-file-base64';

const ProductForm = ( { handleSubmit, form, setForm, image, setImage } ) => {
  const [errors, setErrors] = useState([])

  const setFile = (file) => {
    setImage(file)
    setForm({
      ...form,
      ["image"]: file
    })
  }

  const handleChange = (target) => {
    setForm({
      ...form,
      [target.name]: target.value
    })
  }

  const handleControl = e => {
    e.preventDefault();
    for (const [key, value] of Object.entries(form)) {
      if(!value){
        setErrors([<p>campo {key} Ã¨ vuoto</p>])
      }
    }


    console.log(errors)


    if(!errors){
      handleSubmit(e)
    }
  }

  return(
    <form onSubmit={handleControl}>
      <label>
        <p>Name</p>
        <input type="text" name="name" value={form.name} onChange={e => handleChange(e.target)} />
      </label>
      <label>
        <p>Prezzo</p>
        <input type="number" name="price" value={form.price} onChange={e => handleChange(e.target)} />
      </label>
      <label>
        <p>Immagine</p>
        <FileBase64
          onDone={ setFile.bind(this) } />
      </label>
      <div className="text-center">
        <img src={image.base64} />
      </div>
      { 
        errors.map(error => (
          <p>{error}</p>
        )) 
      }
      <div>
        <button type="submit">Submit</button>
      </div>
    </form>
  )

}

export default ProductForm
