import React, { useEffect, useState } from 'react'
import { useParams, useHistory } from 'react-router-dom'
import { Container } from 'react-bootstrap'
import { getProduct, editProduct } from '../../api/products'

const EditProductPage = () => {
  const history = useHistory()
  const { id } = useParams()
  const [product, setProduct] = useState({});
  const [productId, setProductId] = useState();
  const [name, setName] = useState();
  const [price, setPrice] = useState();

  const fetchDataProduct = async () => {
    const resProduct = await getProduct(id)
    setProduct(resProduct)
    setProductId(resProduct.productId)
    setName(resProduct.name)
    setPrice(resProduct.pice)
  }

  const handleSubmit = async e => {
    e.preventDefault(); 
    const resProduct = await editProduct({
      id: productId,
      name: name,
      price: price
    });
    history.push("/admin/product/"+resProduct.id)
  }

  useEffect( () => {
    fetchDataProduct()
  }, [])

  return (
    <Container>
      <h1>Modifica {product.id}</h1>
      <form onSubmit={handleSubmit}>
        <label>
          <p>Name</p>
          <input type="text" value={name} onChange={e => setName(e.target.value)} />
        </label>
        <label>
          <p>Prezzo</p>
          <input type="number" value={price} onChange={e => setPrice(e.target.value)} />
        </label>
        <div>
          <button type="submit">Submit</button>
        </div>
      </form>
    </Container>
  )

}
export default EditProductPage
