import React, { useState } from 'react'
import { Form, Button, Col, Image } from 'react-bootstrap';
import FileBase64 from 'react-file-base64';

const ProductForm = ( { handleSubmit, form, setForm, image, setImage } ) => {
  state = form
  const [errors, setErrors] = useState({})

  const setFile = (file) => {
    setImage(file)
    setState({
      ...state,
      ["image"]: file
    })
  }

  const isEmptyImage = (obj) => {
    for(var prop in obj) {
      if(obj.hasOwnProperty(prop)) {
        return false;
      }
    }
    return JSON.stringify(obj) === JSON.stringify({});
  }

  const controlForm = (state) => {

    //clean empty ingredients    
    setState({
      ...state,
      ['ingredients']: state.ingredients.filter((ingredient) => {
        return ingredient !== ''
      })
    })

    //validate handle react / react hook / yup / ..
    for (const [key, value] of Object.entries(state)) {
      if(!value){
        setErrors({ [key]: "required*"})
        return false
      } 
    }
    // lodash _.isEmpty({});
    if(isEmptyImage(state.image)){
      setErrors({ ['image']: "required*"})
      return false 
    }

    return true
  }

  const handleChange = (target) => {
    setState({
      ...state,
      [target.name]: target.value
    })
  }

  const handleChangeIngredients = (target) => {
    state.ingredients[target.name] = target.value
    setState({
      ...state,
      ['ingredients']: state.ingredients
    })
  }

  const addIngredient = () => {
    state.ingredients.push('')
    setState({
      ...state,
      ['ingredients']: state.ingredients
    })
  }

  const submitForm = async e => {
    e.preventDefault();
    const control = await controlForm(state)
    if(control){
      console.log(state)
      // handleSubmit(e, {...state})
    }
  }

  return(
    <Form style={{marginTop: '50px'}}>
      <Form.Row>
        <Form.Group as={Col} md={4} xs={12} controlId="GridName">
          <Form.Label>Nome*</Form.Label>
          <Form.Control placeholder="Nome" name="name" value={state.name} onChange={e => handleChange(e.target)} />
          <p>{errors.name}</p>
        </Form.Group>

        <Form.Group as={Col} md={4} xs={12} controlId="GridPrice">
        <Form.Label>Prezzo Iniziale*</Form.Label>
          <Form.Control type='number' placeholder="Prezzo" name="initialPrice" value={state.initialPrice} onChange={e => handleChange(e.target)} />
          <p>{errors.initialPrice}</p>
        </Form.Group>

        <Form.Group as={Col} md={4} xs={12} controlId="GridState">
          <Form.Label>Valuta*</Form.Label>
          <Form.Control as="select" name='currency' value={state.currency} onChange={e => handleChange(e.target)}>
            <option></option>
            <option>€</option>
            <option>$</option>
            <option>£</option>
          </Form.Control>
          <p>{errors.currency}</p>
        </Form.Group>
      </Form.Row>

      <Form.Group as={Col} md={4} xs={12} controlId="GridIngredients">
      <Form.Label>Ingredienti</Form.Label>
        { (state.ingredients && state.ingredients.length > 0) ?
          form.ingredients.map((ingredient, index) => (
            <Form.Control key={index} style={{marginBottom: '15px'}} placeholder="Ingrediente" name={index} value={ingredient} onChange={e => handleChangeIngredients(e.target)} />
          )) : <br />
        }
        <Button variant="primary" type="button" onClick={() => addIngredient()}>Aggiungi</Button>
      </Form.Group>

      <Form.Row>
        <Form.Group as={Col} controlId="Image">
          <Form.Label>Immagine*</Form.Label>
          <br />
          <FileBase64
            onDone={ setFile.bind(this) } />
          <p>{errors.image}</p>
        </Form.Group>
        <Form.Group as={Col}>
          <Image src={image.base64} />
        </Form.Group>
      </Form.Row>

      <Button variant="primary" type="button" onClick={submitForm}>
        Salva
      </Button>
    </Form>  
    )

}

export default ProductForm
