import React, { useEffect, useState } from 'react'
import { useParams, useHistory } from 'react-router-dom'
import { Container } from 'react-bootstrap'
import { getProduct, editProduct } from '../../api/products'

const EditProductPage = () => {
  const history = useHistory()
  const { id } = useParams()
  const [product, setProduct] = useState({});
  const [ isInitialDataLoaded, setIsInitialDataLoaded] = useState(false)
  const defaultData = {
    name: '',
    price: ''
  }
  const [form, setform] = useState(defaultData)



  const fetchDataProduct = async () => {
    const resProduct = await getProduct(id)
    setform()
    setIsInitialDataLoaded(true)
  }

  const handleSubmit = async e => {
    e.preventDefault(); 
    // const resProduct = await editProduct({
    //   id: productId,
    //   name: name,
    //   price: price
    // });
    // history.push("/admin/product/"+resProduct.id)
  }

  const setDataProduct = (target) => {
    let values = product;
    values[target.name] = target.value;
    setProduct(values)
  }

  useEffect(() => {
    fetchDataProduct()
  }, [isInitialDataLoaded])

  return (
    <Container>
      <h1>Modifica {product.name}</h1>
      <form onSubmit={handleSubmit}>
        <label>
          <p>Name</p>
          <input type="text" name='name' value={product.name} onChange={e => setDataProduct( e.target )} />
        </label>
        <label>
          <p>Prezzo</p>
          <input type="number" name='price' value={product.price} onChange={e => setDataProduct( e.target )} />
        </label>
        <div>
          <button type="submit">Submit</button>
        </div>
      </form>
    </Container>
  )

}
export default EditProductPage
