import React, { useEffect, useState } from 'react'
import { useParams, useHistory } from 'react-router-dom'
import { Container } from 'react-bootstrap'
import { getProduct, editProduct } from '../../api/products'
import FileBase64 from 'react-file-base64';

const EditProductPage = () => {
  const history = useHistory()
  const { id } = useParams()
  const [form, setForm] = useState({})
  const [image, setImage] = useState({})

  const setFile = (file) => {
    setImage(file)
    setForm({
      ...form,
      ["image"]: file
    })
  }

  const fetchDataProduct = async () => {
    const resProduct = await getProduct(id)
    setImage(resProduct.image)
    setForm(resProduct)
  }

  const handleSubmit = async e => {
    e.preventDefault(); 
    const resProduct = await editProduct(form);
    history.push("/admin/product/"+resProduct.id)
  }

  const handleChange = (target) => {
    setForm({
      ...form,
      [target.name]: target.value
    })
  }

  useEffect(() => {
    fetchDataProduct()
  }, [])

  return (
    <Container>
      <h1>Modifica {form.name}</h1>
      <form onSubmit={handleSubmit}>
        <label>
          <p>Name</p>
          <input type="text" name='name' value={form.name} onChange={e => handleChange( e.target )} />
        </label>
        <label>
          <p>Prezzo</p>
          <input type="number" name='price' value={form.price} onChange={e => handleChange( e.target )} />
        </label>
        <label>
          <p>Immagine</p>
          <FileBase64
            onDone={ setFile.bind(this) } />
        </label>
        <div className="text-center">
          <img src={image.base64} />
        </div>
        <div>
          <button type="submit">Submit</button>
        </div>
      </form>
    </Container>
  )

}
export default EditProductPage
