import { Image, Table, Nav, Button } from "react-bootstrap";
import { useHistory } from "react-router";
import { Link } from "react-router-dom";
import { deleteProduct } from '../../api/products'


const TableList = ({ products }) => {
  const history = useHistory()

  const removeProduct = async (product) => {
    if(await deleteProduct(product.id)){
      history.push("/admin/dashboard/")
    }
  }

  return (
    <Table striped bordered hover>
      <thead>
        <tr>
          <th></th>
          <th>Nome</th>
          <th>Prezzo</th>
          <th>Azioni</th>
        </tr>
      </thead>
      <tbody>
        { 
          products.map(product => (
            <tr key={product.id}>
              <td><Image src={product.image.base64} style={{ width: "100px" }} /></td>
              <td>{product.name}</td>
              <td>{product.price}{product.currency}</td>
              <td>
                <Link className="btn-action" href={"/admin/product/show/"+product.id}>👁️</Link> 
                <Link className="btn-action" href={"/admin/product/edit/"+product.id}>✏️</Link>
                <Link className="btn-action" onClick={removeProduct(product)}>🗑</Link>  
              </td>
            </tr>
          )) 
        }
      </tbody>
    </Table>

  );
}

export default TableList;
