import React, { useEffect, useState } from 'react'
import { useParams, useHistory } from 'react-router-dom'
import { Container } from 'react-bootstrap'
import { getProduct, editProduct } from '../../api/products'

const EditProductPage = () => {
  const history = useHistory()
  const { id } = useParams()
  const [product, setProduct] = useState({});
  const [producttest, setProductTest] = useState({});
  // const [productId, setProductId] = useState();
  // const [name, setName] = useState();
  // const [price, setPrice] = useState();

  const fetchDataProduct = async () => {
    const resProduct = await getProduct(id)
    setProduct(resProduct)
    setProductTest(resProduct)
    // setProductId(resProduct.id)
    // setName(resProduct.name)
    // setPrice(resProduct.price)
  }

  const handleSubmit = async e => {
    e.preventDefault(); 
    console.log(product)
    // const resProduct = await editProduct({
    //   id: productId,
    //   name: name,
    //   price: price
    // });
    // history.push("/admin/product/"+resProduct.id)
  }

  const setDataProduct = (target) => {
    product[target.name] = target.value +
    setProduct(product)
  }

  useEffect( () => {
    fetchDataProduct()
  }, [])

  return (
    <Container>
      <h1>Modifica {product.name}</h1>
      <form onSubmit={handleSubmit}>
        <label>
          <p>Name</p>
          <input type="text" name='name' value={product.name} onChange={e => setDataProduct( e.target )} />
        </label>
        <label>
          <p>Prezzo</p>
          <input type="number" name='price' value={product.price} onChange={e => setDataProduct( e.target)} />
        </label>
        <div>
          <button type="submit">Submit</button>
        </div>
      </form>
    </Container>
  )

}
export default EditProductPage
